import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner';
import { MapPin, Star, MessageCircle, ChefHat } from 'lucide-react';
import '../styles/RestaurantDetail.css';

const RestaurantDetail = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`/api/restaurants/${id}`);
        setRestaurant(response.data.restaurant);
      } catch (err) {
        setError('Failed to load restaurant details.');
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurant();
  }, [id]);

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <Star key={i} size={20} className="star filled" />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <Star key="half" size={20} className="star half-filled" />
      );
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <Star key={`empty-${i}`} size={20} className="star empty" />
      );
    }

    return stars;
  };

  if (loading) return <LoadingSpinner />;

  if (error) return <div className="error-message">{error}</div>;

  if (!restaurant) return <div className="no-data">No restaurant data found.</div>;

  const {
    name,
    address,
    menuTypes,
    image,
    averageRating,
    totalReviews,
    owner,
    description
  } = restaurant;

  return (
    <div className="restaurant-detail-page">
      <h1 className="restaurant-name">{name}</h1>

      <div className="restaurant-image-container">
        {image ? (
          <img
            src={`/${image}`}
            alt={name}
            className="restaurant-image"
            onError={(e) => {
              e.target.style.display = 'none';
              e.target.nextSibling.style.display = 'flex';
            }}
          />
        ) : null}
        <div className="restaurant-image-placeholder" style={{ display: image ? 'none' : 'flex' }}>
          <ChefHat size={60} />
        </div>
      </div>

      <div className="restaurant-info">
        <div className="restaurant-address">
          <MapPin size={20} />
          <span>{address?.street}, {address?.city}</span>
        </div>

        <div className="restaurant-menu-types">
          {menuTypes && menuTypes.length > 0 ? (
            <>
              {menuTypes.slice(0, 3).map((type, index) => (
                <span key={index} className="menu-type-tag">{type}</span>
              ))}
              {menuTypes.length > 3 && (
                <span className="menu-type-more">+{menuTypes.length - 3} more</span>
              )}
            </>
          ) : (
            <span>No cuisine information</span>
          )}
        </div>

        <div className="restaurant-rating">
          <div className="stars">
            {renderStars(averageRating || 0)}
          </div>
          <span className="rating-text">
            {averageRating > 0 ? averageRating.toFixed(1) : 'No rating'}
          </span>
          <div className="reviews-count">
            <MessageCircle size={18} />
            <span>{totalReviews || 0} reviews</span>
          </div>
        </div>

        <div className="restaurant-owner">
          <span>Added by: {owner?.username || 'Unknown'}</span>
        </div>

        <div className="restaurant-description">
          <h3>Description:</h3>
          <p>{description || 'No description provided.'}</p>
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetail;
