import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';
import { Search, Filter, MapPin, Star, ChefHat, X } from 'lucide-react';
import axios from 'axios';
import RestaurantCard from '../components/RestaurantCard';
import LoadingSpinner from '../components/LoadingSpinner';
import Pagination from '../components/Pagination';
import '../styles/Home.css';

const menuTypes = [
  'Polska', 'Włoska', 'Azjatycka', 'Amerykańska', 'Francuska',
  'Meksykańska', 'Indyjska', 'Grecka', 'Sushi', 'Pizza',
  'Burgery', 'Kebab', 'Vegan', 'Wegetariańska', 'Fast Food',
  'Fine Dining', 'Śniadania', 'Desery', 'Seafood', 'BBQ'
];

const Home = () => {
  const [filters, setFilters] = useState({
    search: '',
    menuType: '',
    sort: 'name',
    order: 'asc',
    page: 1,
    limit: 12
  });

  const [userLocation, setUserLocation] = useState(null);
  const [showFilters, setShowFilters] = useState(false);
  const [searchInput, setSearchInput] = useState('');

  // Pobieranie lokalizacji użytkownika
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.log('Dostęp do lokalizacji odrzucony lub niedostępny');
          // Domyślne współrzędne dla Poznania
          setUserLocation({
            lat: 52.4082,
            lng: 16.9335
          });
        }
      );
    } else {
      // Domyślne współrzędne dla Poznania
      setUserLocation({
        lat: 52.4082,
        lng: 16.9335
      });
    }
  }, []);

  // Debounced search effect
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setFilters(prev => ({
        ...prev,
        search: searchInput,
        page: 1
      }));
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [searchInput]);

  // Pobieranie restauracji z API
  const fetchRestaurants = async () => {
    const params = new URLSearchParams();

    if (filters.search) params.append('search', filters.search);
    if (filters.menuType) params.append('menuType', filters.menuType);
    if (userLocation) {
      params.append('lat', userLocation.lat);
      params.append('lng', userLocation.lng);
      params.append('radius', '50000'); // promień 50 km
    }

    params.append('sort', filters.sort);
    params.append('order', filters.order);
    params.append('page', filters.page);
    params.append('limit', filters.limit);

    const response = await axios.get(`/api/restaurants?${params}`);
    return response.data;
  };

  const { data, isLoading, error, refetch } = useQuery(
    ['restaurants', filters, userLocation],
    fetchRestaurants,
    {
      enabled: !!userLocation,
      keepPreviousData: true
    }
  );

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: key !== 'page' ? 1 : value
    }));
  };

  const handleSearchChange = (e) => {
    setSearchInput(e.target.value);
  };

  const clearSearch = () => {
    setSearchInput('');
    setFilters(prev => ({
      ...prev,
      search: '',
      page: 1
    }));
  };

  const clearFilters = () => {
    setSearchInput('');
    setFilters({
      search: '',
      menuType: '',
      sort: 'name',
      order: 'asc',
      page: 1,
      limit: 12
    });
  };

  const toggleFilters = () => {
    setShowFilters(prev => !prev);
  };

  if (error) {
    return (
      <div className="error-message">
        <p>Błąd ładowania restauracji. Spróbuj ponownie później.</p>
        <button onClick={() => refetch()}>Spróbuj ponownie</button>
      </div>
    );
  }

  return (
    <div className="home-page">
      <div className="hero-section">
        <div className="hero-content">
          <h1>Odkrywaj świetne restauracje</h1>
          <p>Znajdź i oceniaj najlepsze restauracje w swojej okolicy</p>

          <div className="search-bar">
            <div className="search-input-container">
              <Search size={20} />
              <input
                type="text"
                placeholder="Szukaj restauracji po nazwie, adresie lub kuchni..."
                value={searchInput}
                onChange={handleSearchChange}
                className="search-input"
              />
              {searchInput && (
                <button
                  onClick={clearSearch}
                  className="clear-search-btn"
                  style={{
                    position: 'absolute',
                    right: '1rem',
                    background: 'none',
                    border: 'none',
                    color: '#7a2516',
                    cursor: 'pointer',
                    padding: '0.25rem',
                    borderRadius: '4px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: 'background-color 0.2s ease'
                  }}
                  onMouseEnter={(e) => e.target.style.backgroundColor = '#f3f4f6'}
                  onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                >
                  <X size={16} />
                </button>
              )}
            </div>

            <button
              className="filter-toggle"
              onClick={toggleFilters}
            >
              <Filter size={20} />
              {showFilters ? 'Ukryj filtry' : 'Pokaż filtry'}
            </button>
          </div>
        </div>
      </div>

      <div style={{ display: 'flex', gap: 24, padding: '1rem', maxWidth: '1200px', margin: '0 auto' }}>
        {/* Sidebar filtry */}
        {showFilters && (
          <aside className="sidebar-filters">
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1.5rem' }}>
              <h3 style={{ margin: 0 }}>Filtry</h3>
              <button
                onClick={clearFilters}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#a8321f',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.25rem',
                  fontSize: '0.875rem',
                  fontWeight: '600',
                  padding: '0.25rem 0.5rem',
                  borderRadius: '4px',
                  transition: 'background-color 0.2s ease'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#f3f4f6'}
                onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              >
                <X size={14} />
                Wyczyść
              </button>
            </div>

            <div className="filter-group">
              <label htmlFor="menuType">Rodzaj kuchni:</label>
              <select
                id="menuType"
                value={filters.menuType}
                onChange={(e) => handleFilterChange('menuType', e.target.value)}
              >
                <option value="">Wszystkie kuchnie</option>
                {menuTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="sort">Sortuj według:</label>
              <select
                id="sort"
                value={filters.sort}
                onChange={(e) => handleFilterChange('sort', e.target.value)}
              >
                <option value="name">Nazwa</option>
                <option value="averageRating">Ocena</option>
                <option value="totalReviews">Liczba recenzji</option>
                <option value="createdAt">Data dodania</option>
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="order">Kolejność:</label>
              <select
                id="order"
                value={filters.order}
                onChange={(e) => handleFilterChange('order', e.target.value)}
              >
                <option value="asc">Rosnąco</option>
                <option value="desc">Malejąco</option>
              </select>
            </div>

            <button
              onClick={clearFilters}
              className="clear-filters-btn"
            >
              Wyczyść wszystkie filtry
            </button>
          </aside>
        )}

        <main style={{ flexGrow: 1 }}>
          <div className="restaurants-section">
            <div className="section-header">
              <ChefHat size={24} color="#a8321f" />
              <h2>Restauracje</h2>
              {userLocation && (
                <span className="location-indicator">
                  <MapPin size={16} />
                  Blisko Ciebie
                </span>
              )}
            </div>

            {/* Aktywne filtry */}
            {(filters.search || filters.menuType) && (
              <div style={{ 
                display: 'flex', 
                flexWrap: 'wrap', 
                gap: '0.5rem', 
                marginBottom: '1rem',
                alignItems: 'center'
              }}>
                <span style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: '500' }}>
                  Aktywne filtry:
                </span>
                {filters.search && (
                  <span style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem',
                    background: '#f3f4f6',
                    padding: '0.25rem 0.5rem',
                    borderRadius: '6px',
                    fontSize: '0.875rem',
                    color: '#374151'
                  }}>
                    "{filters.search}"
                    <button
                      onClick={() => {
                        setSearchInput('');
                        handleFilterChange('search', '');
                      }}
                      style={{
                        background: 'none',
                        border: 'none',
                        color: '#6b7280',
                        cursor: 'pointer',
                        padding: '0.125rem',
                        borderRadius: '2px',
                        display: 'flex'
                      }}
                    >
                      <X size={12} />
                    </button>
                  </span>
                )}
                {filters.menuType && (
                  <span style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem',
                    background: '#f3f4f6',
                    padding: '0.25rem 0.5rem',
                    borderRadius: '6px',
                    fontSize: '0.875rem',
                    color: '#374151'
                  }}>
                    {filters.menuType}
                    <button
                      onClick={() => handleFilterChange('menuType', '')}
                      style={{
                        background: 'none',
                        border: 'none',
                        color: '#6b7280',
                        cursor: 'pointer',
                        padding: '0.125rem',
                        borderRadius: '2px',
                        display: 'flex'
                      }}
                    >
                      <X size={12} />
                    </button>
                  </span>
                )}
              </div>
            )}

            {data && (
              <p className="results-count">
                Znaleziono {data.pagination.total} restauracji
                {filters.search && ` dla "${filters.search}"`}
                {filters.menuType && ` w kategorii "${filters.menuType}"`}
              </p>
            )}

            {isLoading ? (
              <LoadingSpinner />
            ) : (
              <>
                <div className="restaurants-grid">
                  {data?.restaurants?.map(restaurant => (
                    <RestaurantCard key={restaurant._id} restaurant={restaurant} />
                  ))}
                </div>

                {data?.restaurants?.length === 0 && (
                  <div className="no-results">
                    <ChefHat size={48} />
                    <h3>Nie znaleziono restauracji</h3>
                    <p>
                      {filters.search || filters.menuType 
                        ? 'Spróbuj zmienić kryteria wyszukiwania lub filtry'
                        : 'Spróbuj ponownie później'
                      }
                    </p>
                    {(filters.search || filters.menuType) && (
                      <button
                        onClick={clearFilters}
                        style={{
                          marginTop: '1rem',
                          background: '#a8321f',
                          color: 'white',
                          border: 'none',
                          padding: '0.5rem 1rem',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontWeight: '600'
                        }}
                      >
                        Wyczyść filtry
                      </button>
                    )}
                  </div>
                )}

                {data?.pagination && data.pagination.pages > 1 && (
                  <Pagination
                    currentPage={data.pagination.page}
                    totalPages={data.pagination.pages}
                    onPageChange={(page) => handleFilterChange('page', page)}
                  />
                )}
              </>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Home;
