import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Search, Filter, MapPin, Star, ChefHat } from 'lucide-react';
import RestaurantCard from '../components/RestaurantCard';
import LoadingSpinner from '../components/LoadingSpinner';
import Pagination from '../components/Pagination';
import '../styles/Home.css';

const menuTypes = [
  'Polska', 'Włoska', 'Azjatycka', 'Amerykańska', 'Francuska', 
  'Meksykańska', 'Indyjska', 'Grecka', 'Sushi', 'Pizza', 
  'Burgery', 'Kebab', 'Vegan', 'Wegetariańska', 'Fast Food',
  'Fine Dining', 'Śniadania', 'Desery', 'Seafood', 'BBQ'
];

const Home = () => {
  const [filters, setFilters] = useState({
    search: '',
    menuType: '',
    sort: 'name',
    order: 'asc',
    page: 1,
    limit: 12
  });

  const [userLocation, setUserLocation] = useState(null);
  const [showFilters, setShowFilters] = useState(false);

  // Get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.log('Location access denied or unavailable');
          // Default to Poznań coordinates
          setUserLocation({
            lat: 52.4082,
            lng: 16.9335
          });
        }
      );
    } else {
      // Default to Poznań coordinates
      setUserLocation({
        lat: 52.4082,
        lng: 16.9335
      });
    }
  }, []);

  const fetchRestaurants = async () => {
    const params = new URLSearchParams();
    
    if (filters.search) params.append('search', filters.search);
    if (filters.menuType) params.append('menuType', filters.menuType);
    if (userLocation) {
      params.append('lat', userLocation.lat);
      params.append('lng', userLocation.lng);
      params.append('radius', '50000'); // 50km radius
    }
    
    params.append('sort', filters.sort);
    params.append('order', filters.order);
    params.append('page', filters.page);
    params.append('limit', filters.limit);

    const response = await axios.get(`/api/restaurants?${params}`);
    return response.data;
  };

  const { data, isLoading, error, refetch } = useQuery(
    ['restaurants', filters, userLocation],
    fetchRestaurants,
    {
      enabled: !!userLocation,
      keepPreviousData: true
    }
  );

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: key !== 'page' ? 1 : value
    }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      menuType: '',
      sort: 'name',
      order: 'asc',
      page: 1,
      limit: 12
    });
  };

  if (error) {
    return (
      <div className="error-message">
        <p>Error loading restaurants. Please try again later.</p>
        <button onClick={() => refetch()}>Retry</button>
      </div>
    );
  }

  return (
    <div className="home-page">
      <div className="hero-section">
        <div className="hero-content">
          <h1>Discover Great Restaurants</h1>
          <p>Find and review the best restaurants in your area</p>
          
          <div className="search-bar">
            <div className="search-input-container">
              <Search size={20} />
              <input
                type="text"
                placeholder="Search restaurants by name, address, or cuisine..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="search-input"
              />
            </div>
            
            <button 
              className="filter-toggle"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter size={20} />
              Filters
            </button>
          </div>
        </div>
      </div>

      {showFilters && (
        <div className="filters-section">
          <div className="filters-container">
            <div className="filter-group">
              <label>Cuisine Type:</label>
              <select
                value={filters.menuType}
                onChange={(e) => handleFilterChange('menuType', e.target.value)}
              >
                <option value="">All Cuisines</option>
                {menuTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>Sort by:</label>
              <select
                value={filters.sort}
                onChange={(e) => handleFilterChange('sort', e.target.value)}
              >
                <option value="name">Name</option>
                <option value="averageRating">Rating</option>
                <option value="totalReviews">Number of Reviews</option>
                <option value="createdAt">Date Added</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Order:</label>
              <select
                value={filters.order}
                onChange={(e) => handleFilterChange('order', e.target.value)}
              >
                <option value="asc">Ascending</option>
                <option value="desc">Descending</option>
              </select>
            </div>

            <button onClick={clearFilters} className="clear-filters">
              Clear Filters
            </button>
          </div>
        </div>
      )}

      <div className="restaurants-section">
        <div className="section-header">
          <h2>
            <ChefHat size={24} />
            Restaurants
            {userLocation && (
              <span className="location-indicator">
                <MapPin size={16} />
                Near You
              </span>
            )}
          </h2>
          
          {data && (
            <p className="results-count">
              Found {data.pagination.total} restaurants
            </p>
          )}
        </div>

        {isLoading ? (
          <LoadingSpinner />
        ) : (
          <>
            <div className="restaurants-grid">
              {data?.restaurants?.map(restaurant => (
                <RestaurantCard key={restaurant._id} restaurant={restaurant} />
              ))}
            </div>

            {data?.restaurants?.length === 0 && (
              <div className="no-results">
                <ChefHat size={48} />
                <h3>No restaurants found</h3>
                <p>Try adjusting your search criteria or filters</p>
              </div>
            )}

            {data?.pagination && data.pagination.pages > 1 && (
              <Pagination
                currentPage={data.pagination.page}
                totalPages={data.pagination.pages}
                onPageChange={(page) => handleFilterChange('page', page)}
              />
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Home;
