import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';
import { Search, Filter, MapPin, Star, ChefHat } from 'lucide-react';
import axios from 'axios';
import RestaurantCard from '../components/RestaurantCard';
import LoadingSpinner from '../components/LoadingSpinner';
import Pagination from '../components/Pagination';
import '../styles/Home.css';

const menuTypes = [
  'Polska', 'Włoska', 'Azjatycka', 'Amerykańska', 'Francuska',
  'Meksykańska', 'Indyjska', 'Grecka', 'Sushi', 'Pizza',
  'Burgery', 'Kebab', 'Vegan', 'Wegetariańska', 'Fast Food',
  'Fine Dining', 'Śniadania', 'Desery', 'Seafood', 'BBQ'
];

const Home = () => {
  const [filters, setFilters] = useState({
    search: '',
    menuType: '',
    sort: 'name',
    order: 'asc',
    page: 1,
    limit: 12
  });

  const [userLocation, setUserLocation] = useState(null);
  const [showFilters, setShowFilters] = useState(false);

  // Pobieranie lokalizacji użytkownika
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.log('Dostęp do lokalizacji odrzucony lub niedostępny');
          // Domyślne współrzędne dla Poznania
          setUserLocation({
            lat: 52.4082,
            lng: 16.9335
          });
        }
      );
    } else {
      // Domyślne współrzędne dla Poznania
      setUserLocation({
        lat: 52.4082,
        lng: 16.9335
      });
    }
  }, []);

  // Pobieranie restauracji z API
  const fetchRestaurants = async () => {
    const params = new URLSearchParams();

    if (filters.search) params.append('search', filters.search);
    if (filters.menuType) params.append('menuType', filters.menuType);
    if (userLocation) {
      params.append('lat', userLocation.lat);
      params.append('lng', userLocation.lng);
      params.append('radius', '50000'); // promień 50 km
    }

    params.append('sort', filters.sort);
    params.append('order', filters.order);
    params.append('page', filters.page);
    params.append('limit', filters.limit);

    const response = await axios.get(`/api/restaurants?${params}`);
    // console.log(response.data);
    return response.data;
  };

  const { data, isLoading, error, refetch } = useQuery(
    ['restaurants', filters, userLocation],
    fetchRestaurants,
    {
      enabled: !!userLocation,
      keepPreviousData: true
    }
  );

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: key !== 'page' ? 1 : value
    }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      menuType: '',
      sort: 'name',
      order: 'asc',
      page: 1,
      limit: 12
    });
  };

  if (error) {
    return (
      <div className="error-message">
        <p>Błąd ładowania restauracji. Spróbuj ponownie później.</p>
        <button onClick={() => refetch()}>Spróbuj ponownie</button>
      </div>
    );
  }

  return (
    <div className="home-page">
      <div className="hero-section">
        <div className="hero-content">
          <h1>Odkrywaj świetne restauracje</h1>
          <p>Znajdź i oceniaj najlepsze restauracje w swojej okolicy</p>

          {/* <div className="search-bar">
            <div className="search-input-container">
              <Search size={20} />
              <input
                type="text"
                placeholder="Szukaj restauracji po nazwie, adresie lub kuchni..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="search-input"
              />
            </div>

            <button
              className="filter-toggle"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter size={20} />
              Filtry
            </button>
          </div> */}
        </div>
      </div>

      <div className="content-wrapper" style={{ display: 'flex', gap: 24, padding: '1rem' }}>
        {/* Sidebar filtry - widoczne na desktop, na mobile pod przyciskiem */}
        {(showFilters || window.innerWidth >= 768) && (
          <aside className="sidebar-filters" style={{
            minWidth: 280,
            backgroundColor: '#fff',
            border: '1px solid #ddd',
            borderRadius: 8,
            padding: 16,
            height: 'fit-content'
          }}>
            <h3 style={{ marginBottom: 12 }}>Filtry</h3>

            <div className="filter-group" style={{ marginBottom: 16 }}>
              <label htmlFor="menuType" style={{ display: 'block', marginBottom: 6 }}>Rodzaj kuchni:</label>
              <select
                id="menuType"
                value={filters.menuType}
                onChange={(e) => handleFilterChange('menuType', e.target.value)}
                style={{ width: '100%', padding: 6, borderRadius: 4 }}
              >
                <option value="">Wszystkie kuchnie</option>
                {menuTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>

            <div className="filter-group" style={{ marginBottom: 16 }}>
              <label htmlFor="sort" style={{ display: 'block', marginBottom: 6 }}>Sortuj według:</label>
              <select
                id="sort"
                value={filters.sort}
                onChange={(e) => handleFilterChange('sort', e.target.value)}
                style={{ width: '100%', padding: 6, borderRadius: 4 }}
              >
                <option value="name">Nazwa</option>
                <option value="averageRating">Ocena</option>
                <option value="totalReviews">Liczba recenzji</option>
                <option value="createdAt">Data dodania</option>
              </select>
            </div>

            <div className="filter-group" style={{ marginBottom: 16 }}>
              <label htmlFor="order" style={{ display: 'block', marginBottom: 6 }}>Kolejność:</label>
              <select
                id="order"
                value={filters.order}
                onChange={(e) => handleFilterChange('order', e.target.value)}
                style={{ width: '100%', padding: 6, borderRadius: 4 }}
              >
                <option value="asc">Rosnąco</option>
                <option value="desc">Malejąco</option>
              </select>
            </div>

            <button
              onClick={clearFilters}
              style={{
                backgroundColor: '#e53e3e',
                color: 'white',
                padding: '8px 12px',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
                width: '100%',
                fontWeight: 'bold'
              }}
            >
              Wyczyść filtry
            </button>
          </aside>
        )}

        <main style={{ flexGrow: 1 }}>
          <div className="restaurants-section">
            <div className="section-header" style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 12 }}>
              <ChefHat size={24} />
              <h2 style={{ margin: 0, fontWeight: 'bold' }}>Restauracje</h2>
              {userLocation && (
                <span className="location-indicator" style={{ display: 'flex', alignItems: 'center', gap: 4, color: '#4a5568' }}>
                  <MapPin size={16} />
                  Blisko Ciebie
                </span>
              )}
            </div>

            {data && (
              <p className="results-count" style={{ marginBottom: 12 }}>
                Znaleziono {data.pagination.total} restauracji
              </p>
            )}

            {isLoading ? (
              <LoadingSpinner />
            ) : (
              <>
                <div className="restaurants-grid" style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fill,minmax(280px,1fr))',
                  gap: 16,
                }}>
                  {data?.restaurants?.map(restaurant => (
                    <RestaurantCard key={restaurant._id} restaurant={restaurant} />
                  ))}
                </div>

                {data?.restaurants?.length === 0 && (
                  <div className="no-results" style={{ textAlign: 'center', marginTop: 40, color: '#718096' }}>
                    <ChefHat size={48} />
                    <h3>Nie znaleziono restauracji</h3>
                    <p>Spróbuj zmienić kryteria wyszukiwania lub filtry</p>
                  </div>
                )}

                {data?.pagination && data.pagination.pages > 1 && (
                  <Pagination
                    currentPage={data.pagination.page}
                    totalPages={data.pagination.pages}
                    onPageChange={(page) => handleFilterChange('page', page)}
                  />
                )}
              </>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Home;
