import React, { useState, useEffect } from 'react';
import { X, Star } from 'lucide-react';
import '../styles/ReviewModal.css';

const ReviewModal = ({ 
  isOpen, 
  onClose, 
  onSubmit, 
  restaurant, 
  existingReview = null,
  isEditing = false 
}) => {
  const [rating, setRating] = useState(existingReview?.rating || 0);
  const [comment, setComment] = useState(existingReview?.comment || '');
  const [hoverRating, setHoverRating] = useState(0);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (existingReview) {
      setRating(existingReview.rating);
      setComment(existingReview.comment || '');
    } else {
      setRating(0);
      setComment('');
    }
    setError('');
  }, [existingReview, isOpen]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape' && !isSubmitting) {
        onClose();
      }
    };
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      window.addEventListener('keydown', handleKeyDown);
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [isOpen, isSubmitting, onClose]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (rating === 0) {
      setError('Ocena jest wymagana');
      return;
    }

    setIsSubmitting(true);
    setError('');

    try {
      await onSubmit({
        rating,
        comment: comment.trim(),
        restaurantId: restaurant.id
      });
      
      setRating(0);
      setComment('');
      onClose();
    } catch (err) {
      setError(err.message || 'Wystąpił błąd podczas zapisywania opinii');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (!isSubmitting) {
      setRating(existingReview?.rating || 0);
      setComment(existingReview?.comment || '');
      setError('');
      onClose();
    }
  };

  const formatAddress = (address) => {
    if (!address) return '';
    return [address.street, address.postalCode, address.city, address.country]
        .filter(Boolean)
        .join(', ');
  };

  const getRatingText = (rating) => {
    const texts = ['Bardzo słabo', 'Słabo', 'Średnio', 'Dobrze', 'Bardzo dobrze'];
    return texts[rating - 1] || '';
  };

  if (!isOpen) return null;

  return (
    <div className="review-modal-overlay" onClick={handleClose}>
      <div 
        className="review-modal-content" 
        onClick={(e) => e.stopPropagation()}
        role="dialog"
        aria-modal="true"
        aria-labelledby="review-modal-title"
      >
        {/* Header */}
        <div className="review-modal-header">
          <h2 id="review-modal-title" className="review-modal-title">
            {isEditing ? 'Edytuj opinię' : 'Dodaj opinię'}
          </h2>
          <button
            onClick={handleClose}
            disabled={isSubmitting}
            aria-label="Zamknij modal"
            className="review-modal-close"
          >
            <X size={24} />
          </button>
        </div>

        {/* Restaurant Info */}
        <div className="restaurant-info">
          <h3 className="restaurant-name">{restaurant.name}</h3>
          <p className="restaurant-address">{formatAddress(restaurant.address)}</p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="review-form">
          {error && (
            <div className="error-message" role="alert">
              {error}
            </div>
          )}

          {/* Rating */}
          <div className="form-group">
            <label className="form-label">
              Ocena <span className="required">*</span>
            </label>
            <div className="star-rating">
              {[1, 2, 3, 4, 5].map((star) => (
                <button
                  key={star}
                  type="button"
                  onClick={() => setRating(star)}
                  onMouseEnter={() => setHoverRating(star)}
                  onMouseLeave={() => setHoverRating(0)}
                  disabled={isSubmitting}
                  aria-label={`Ocena ${star} ${star === 1 ? 'gwiazdka' : star < 5 ? 'gwiazdki' : 'gwiazdek'}`}
                  className="star-button"
                >
                  <Star
                    size={32}
                    className={`star-icon ${
                      star <= (hoverRating || rating) ? 'filled' : 'empty'
                    }`}
                  />
                </button>
              ))}
            </div>
            {(rating > 0 || hoverRating > 0) && (
              <p className="rating-text">
                {getRatingText(hoverRating || rating)}
              </p>
            )}
          </div>

          {/* Comment */}
          <div className="form-group">
            <label htmlFor="comment" className="form-label">
              Komentarz <span className="optional">(opcjonalnie)</span>
            </label>
            <textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              rows={4}
              maxLength={500}
              placeholder="Podziel się swoimi wrażeniami z wizyty w tej restauracji..."
              className="form-textarea"
              disabled={isSubmitting}
            />
            <div className="character-count">
              <span className={comment.length > 450 ? 'warning' : ''}>
                {comment.length}/500
              </span>
            </div>
          </div>

          {/* Actions */}
          <div className="form-actions">
            <button
              type="button"
              onClick={handleClose}
              disabled={isSubmitting}
              className="btn btn-secondary"
            >
              Anuluj
            </button>
            <button
              type="submit"
              disabled={isSubmitting || rating === 0}
              className="btn btn-primary"
            >
              {isSubmitting ? (
                <>
                  <span className="loading-spinner"></span>
                  Zapisywanie...
                </>
              ) : (
                isEditing ? 'Zapisz zmiany' : 'Dodaj opinię'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReviewModal;
