import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Profile.css';

const Profile = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const [restaurantsRes, reviewsRes] = await Promise.all([
          axios.get('/api/user/restaurants'),
          axios.get('/api/user/reviews')
        ]);
        setRestaurants(restaurantsRes.data);
        setReviews(reviewsRes.data);
      } catch (err) {
        setError('Nie udało się pobrać danych użytkownika.');
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="profile-page">
      <h1>Twój profil</h1>
      <p>Tu możesz zobaczyć i edytować swoje informacje profilowe.</p>

      {error && <p className="error-message">{error}</p>}

      {/* Twoje restauracje */}
      <section className="user-section">
        <h2>Twoje restauracje</h2>
        {restaurants.length === 0 ? (
          <p>Brak dodanych restauracji.</p>
        ) : (
          <ul className="user-list">
            {restaurants.map((restaurant) => (
              <li key={restaurant._id}>{restaurant.name}</li>
            ))}
          </ul>
        )}
      </section>

      {/* Twoje recenzje */}
      <section className="user-section">
        <h2>Twoje recenzje</h2>
        {reviews.length === 0 ? (
          <p>Brak napisanych recenzji.</p>
        ) : (
          <ul className="user-list">
            {reviews.map((review) => (
              <li key={review._id}>
                „{review.comment}” – {review.restaurantName}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default Profile;
