import React, { useState, useEffect, useRef } from 'react';
import { Search, MessageCircle, Star, Calendar, User, X } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const SearchReviews = ({ onClose }) => {
  const { isAuthenticated, token } = useAuth(); // token do autoryzacji, jeśli potrzebny

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchHistory, setSearchHistory] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const searchInputRef = useRef(null);

  useEffect(() => {
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, []);

  const highlightText = (text, searchTerm) => {
    if (!searchTerm) return text;
    const regex = new RegExp(`(${searchTerm})`, 'gi');
    const parts = text.split(regex);
    return parts.map((part, index) =>
      regex.test(part) ? (
        <span key={index} className="bg-yellow-200 font-medium">
          {part}
        </span>
      ) : (
        part
      )
    );
  };

  const performSearch = async (term) => {
    if (!term.trim()) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    setIsSearching(true);

    try {
      const response = await fetch(`/api/reviews/search?query=${encodeURIComponent(term)}&limit=10&page=1`, {
        headers: {
          Authorization: `Bearer ${token}`, // jeśli API wymaga tokena
        },
      });

      if (!response.ok) {
        throw new Error('Błąd podczas pobierania danych');
      }

      const data = await response.json();

      // Dane z backendu mają strukturę: { reviews: [...], pagination: {...} }
      // mapujemy je na format zgodny z komponentem
      const results = data.reviews.map((review) => ({
        id: review._id,
        text: review.comment,
        rating: review.rating,
        restaurantName: review.restaurant.name,
        restaurantId: review.restaurant._id,
        authorName: review.user.username,
        date: review.createdAt,
        highlighted: highlightText(review.comment, term),
      }));

      setSearchResults(results);
      setShowResults(true);
      setIsSearching(false);

      // Aktualizacja historii wyszukiwania
      if (term.trim() && !searchHistory.includes(term.trim())) {
        setSearchHistory((prev) => [term.trim(), ...prev].slice(0, 5));
      }
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
      setShowResults(true);
      setIsSearching(false);
    }
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    performSearch(value);
  };

  const handleHistoryClick = (term) => {
    setSearchTerm(term);
    performSearch(term);
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSearchResults([]);
    setShowResults(false);
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('pl-PL', {
      day: 'numeric',
      month: 'long',
      year: 'numeric',
    });
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`w-4 h-4 ${
          i < rating ? 'text-yellow-400 fill-current' : 'text-gray-300'
        }`}
      />
    ));
  };

  if (!isAuthenticated) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg p-6 max-w-md w-full">
          <div className="text-center">
            <MessageCircle className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h2 className="text-xl font-semibold mb-2">Zaloguj się</h2>
            <p className="text-gray-600 mb-4">
              Aby wyszukiwać komentarze, musisz być zalogowany.
            </p>
            <button
              onClick={onClose}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Zamknij
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <div className="flex items-center space-x-2">
            <MessageCircle className="w-6 h-6 text-blue-600" />
            <h2 className="text-xl font-semibold">Wyszukiwanie komentarzy</h2>
          </div>
        </div>

        {/* Search Input */}
        <div className="p-6 border-b">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              ref={searchInputRef}
              type="text"
              placeholder="Wyszukaj komentarze..."
              value={searchTerm}
              onChange={handleSearchChange}
              className="w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            {searchTerm && (
              <button
                onClick={clearSearch}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              >
                <X className="w-5 h-5" />
              </button>
            )}
          </div>

          {/* Search History */}
          {searchHistory.length > 0 && !showResults && (
            <div className="mt-4">
              <p className="text-sm text-gray-600 mb-2">Ostatnie wyszukiwania:</p>
              <div className="flex flex-wrap gap-2">
                {searchHistory.map((term, index) => (
                  <button
                    key={index}
                    onClick={() => handleHistoryClick(term)}
                    className="px-3 py-1 bg-gray-100 rounded-full text-sm text-gray-700 hover:bg-gray-200"
                  >
                    {term}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Search Results */}
        <div className="p-6 overflow-y-auto max-h-[60vh]">
          {isSearching ? (
            <p className="text-gray-600">Wyszukiwanie...</p>
          ) : showResults ? (
            searchResults.length > 0 ? (
              <ul className="space-y-4">
                {searchResults.map((comment) => (
                  <li
                    key={comment.id}
                    className="border rounded-lg p-4 shadow-sm hover:shadow-md transition"
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center space-x-2 text-sm text-gray-600">
                        <User className="w-4 h-4" />
                        <span>{comment.authorName}</span>
                      </div>
                      <div className="flex items-center space-x-2 text-sm text-gray-600">
                        <Calendar className="w-4 h-4" />
                        <span>{formatDate(comment.date)}</span>
                      </div>
                    </div>
                    <div className="mb-2 text-gray-800">{comment.highlighted}</div>
                    <div className="flex items-center space-x-2 mt-2">
                      {renderStars(comment.rating)}
                      <span className="text-sm text-gray-500">
                        – {comment.restaurantName}
                      </span>
                    </div>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-600">Brak wyników dla „{searchTerm}”.</p>
            )
          ) : (
            <p className="text-gray-500 text-sm">Zacznij wpisywać, aby zobaczyć wyniki.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchReviews;
