import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner';

const RestaurantDetail = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`/api/restaurants/${id}`);
        setRestaurant(response.data);
      } catch (err) {
        setError('Failed to load restaurant details.');
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurant();
  }, [id]);

  if (loading) return <LoadingSpinner />;

  if (error) return <div className="error-message">{error}</div>;

  if (!restaurant) return <div className="no-data">No restaurant data found.</div>;

  return (
    <div className="restaurant-detail-page">
      <h1 className="restaurant-name">{restaurant.name}</h1>

      <div className="restaurant-info">
        <h3>Cuisine Types:</h3>
        <ul className="cuisine-list">
          {restaurant.menuTypes && restaurant.menuTypes.length > 0 ? (
            restaurant.menuTypes.map((type, index) => (
              <li key={index}>{type}</li>
            ))
          ) : (
            <li>No cuisine information</li>
          )}
        </ul>

        <h3>Address:</h3>
        {restaurant.address ? (
          <p>
            {restaurant.address.street}, {restaurant.address.city} <br />
            {restaurant.address.postalCode && `${restaurant.address.postalCode}`} <br />
            {restaurant.address.country && restaurant.address.country}
          </p>
        ) : (
          <p>No address available</p>
        )}

        <h3>Average Rating:</h3>
        <p>{restaurant.averageRating ? restaurant.averageRating.toFixed(1) : 'No rating yet'}</p>

        <h3>Description:</h3>
        <p>{restaurant.description || 'No description provided.'}</p>

        {/* Tutaj możesz dodać więcej sekcji np. recenzje, galerię zdjęć, mapę */}
      </div>
    </div>
  );
};

export default RestaurantDetail;
