import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const [profileRes, restaurantsRes, reviewsRes] = await Promise.all([
          axios.get('/api/auth/me'),
          axios.get('/api/restaurants/my-restaurants'),
          axios.get('/api/reviews/my-reviews')
        ]);
        setProfile(profileRes.data.user);                 // Profil użytkownika
        setRestaurants(restaurantsRes.data.restaurants);  // Twoje restauracje
        setReviews(reviewsRes.data.reviews);              // Twoje recenzje
      } catch (err) {
        setError('Nie udało się pobrać danych użytkownika.');
      }
    };

    fetchUserData();
  }, []);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!profile) {
    return <p>Ładowanie profilu...</p>;
  }

  return (
    <div className="profile-page" style={{ display: 'flex', gap: '2rem' }}>
      
      {/* Lewa kolumna - dane profilu */}
      <div style={{ flex: 1, borderRight: '1px solid #ccc', paddingRight: '2rem' }}>
        <h1>Twój profil</h1>
        <p><strong>Nazwa użytkownika:</strong> {profile.username}</p>
        <p><strong>Email:</strong> {profile.email}</p>
        <p><strong>Data utworzenia konta:</strong> {new Date(profile.createdAt).toLocaleDateString()}</p>
        {/* Dodaj inne pola jeśli potrzebujesz */}
      </div>

      {/* Prawa kolumna - restauracje i recenzje */}
      <div style={{ flex: 2 }}>
        <section className="user-section">
          <h2>Twoje restauracje</h2>
          {restaurants.length === 0 ? (
            <p>Brak dodanych restauracji.</p>
          ) : (
            <ul className="user-list" style={{ listStyle: 'none', padding: 0 }}>
              {restaurants.map((restaurant) => (
                <li key={restaurant._id} style={{ marginBottom: '0.5rem' }}>
                  <Link to={`/restaurant/${restaurant._id}`} style={{ textDecoration: 'none', color: 'blue' }}>
                    {restaurant.name}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </section>

        <section className="user-section" style={{ marginTop: '2rem' }}>
          <h2>Twoje recenzje</h2>
          {reviews.length === 0 ? (
            <p>Brak napisanych recenzji.</p>
          ) : (
            <ul className="user-list" style={{ listStyle: 'none', padding: 0 }}>
              {reviews.map((review) => (
                <li key={review._id} style={{ marginBottom: '0.5rem' }}>
                  „{review.comment}” –{' '}
                  <Link to={`/restaurant/${review.restaurantId}`} style={{ color: 'blue' }}>
                    {review.restaurantName}
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </section>
      </div>
    </div>
  );
};

export default Profile;
