import React, { useState, useEffect, useRef } from 'react';
import { Search, MessageCircle, Star, Calendar, User, X } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import '../styles/SearchReviews.css';

const SearchReviews = () => {
  const { isAuthenticated, token } = useAuth();

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchHistory, setSearchHistory] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const searchInputRef = useRef(null);

  useEffect(() => {
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, []);

  const highlightText = (text, searchTerm) => {
    if (!searchTerm) return text;
    const regex = new RegExp(`(${searchTerm})`, 'gi');
    const parts = text.split(regex);
    return parts.map((part, index) =>
      regex.test(part) ? (
        <span key={index} className="highlighted-text">
          {part}
        </span>
      ) : (
        part
      )
    );
  };

  const performSearch = async (term) => {
    if (!term.trim()) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    setIsSearching(true);

    try {
      const response = await fetch(`/api/reviews/search?query=${encodeURIComponent(term)}&limit=10&page=1`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Błąd podczas pobierania danych');
      }

      const data = await response.json();

      const results = data.reviews.map((review) => ({
        id: review._id,
        text: review.comment,
        rating: review.rating,
        restaurantName: review.restaurant.name,
        restaurantId: review.restaurant._id,
        authorName: review.user.username,
        date: review.createdAt,
        highlighted: highlightText(review.comment, term),
      }));

      setSearchResults(results);
      setShowResults(true);
      setIsSearching(false);

      if (term.trim() && !searchHistory.includes(term.trim())) {
        setSearchHistory((prev) => [term.trim(), ...prev].slice(0, 5));
      }
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
      setShowResults(true);
      setIsSearching(false);
    }
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    performSearch(value);
  };

  const handleHistoryClick = (term) => {
    setSearchTerm(term);
    performSearch(term);
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSearchResults([]);
    setShowResults(false);
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('pl-PL', {
      day: 'numeric',
      month: 'long',
      year: 'numeric',
    });
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={i < rating ? 'star-filled' : 'star-empty'}
      />
    ));
  };

  if (!isAuthenticated) {
    return (
      <div className="searchreviews-not-authenticated">
        <MessageCircle className="icon-large gray" />
        <h2>Zaloguj się</h2>
        <p>Aby wyszukiwać komentarze, musisz być zalogowany.</p>
      </div>
    );
  }

  return (
    <div className="searchreviews-container">
      {/* Header */}
      <div className="searchreviews-header">
        <MessageCircle className="icon-blue" />
        <h2>Wyszukiwanie komentarzy</h2>
      </div>

      {/* Search Input */}
      <div className="searchreviews-search-section">
        <Search className="icon-left" />
        <input
          ref={searchInputRef}
          type="text"
          placeholder="Wyszukaj komentarze..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        {searchTerm && (
          <button onClick={clearSearch} className="icon-right-button" aria-label="Wyczyść wyszukiwanie">
            <X />
          </button>
        )}

        {/* Search History */}
        {searchHistory.length > 0 && !showResults && (
          <div className="search-history">
            <p>Ostatnie wyszukiwania:</p>
            <div>
              {searchHistory.map((term, index) => (
                <button key={index} onClick={() => handleHistoryClick(term)}>
                  {term}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Search Results */}
      <div className="search-results">
        {isSearching ? (
          <p>Wyszukiwanie...</p>
        ) : showResults ? (
          searchResults.length > 0 ? (
            <ul>
              {searchResults.map((comment) => (
                <li key={comment.id}>
                  <div className="review-meta">
                    <div className="flex items-center">
                      <User className="icon-small" />
                      <span>{comment.authorName}</span>
                    </div>
                    <div className="flex items-center">
                      <Calendar className="icon-small" />
                      <span>{formatDate(comment.date)}</span>
                    </div>
                  </div>
                  <div className="review-text">{comment.highlighted}</div>
                  <div className="review-rating">
                    {renderStars(comment.rating)}
                    <span className="restaurant-name">– {comment.restaurantName}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>Brak wyników dla „{searchTerm}”.</p>
          )
        ) : (
          <p className="text-gray-500 text-sm">Zacznij wpisywać, aby zobaczyć wyniki.</p>
        )}
      </div>
    </div>
  );
};

export default SearchReviews;
