import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddRestaurant = () => {
  const [formData, setFormData] = useState({
    name: '',
    menuType: '',
    address: {
      street: '',
      city: '',
      postalCode: '',
      country: ''
    },
    description: '',
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('address.')) {
      const addressField = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        address: {
          ...prev.address,
          [addressField]: value,
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      await axios.post('/api/restaurants', formData);
      navigate('/');
    } catch (err) {
      setError('Failed to add restaurant. Please try again.');
    }
  };

  // Prosty styl dla pionowego układu:
  const labelStyle = {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '16px',
  };

  const inputStyle = {
    padding: '8px',
    fontSize: '16px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  };

  const textareaStyle = {
    ...inputStyle,
    resize: 'vertical',
  };

  return (
    <div style={{ maxWidth: '400px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '24px' }}>Add New Restaurant</h1>
      {error && <p style={{ color: 'red', marginBottom: '16px' }}>{error}</p>}
      <form onSubmit={handleSubmit}>

        <label style={labelStyle}>
          Name:
          <input
            style={inputStyle}
            name="name"
            type="text"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>

        <label style={labelStyle}>
          Cuisine Type:
          <input
            style={inputStyle}
            name="menuType"
            type="text"
            value={formData.menuType}
            onChange={handleChange}
            required
          />
        </label>

        <fieldset style={{ marginBottom: '16px', border: 'none', padding: 0 }}>
          <label style={labelStyle}>
            Street:
            <input
              style={inputStyle}
              name="address.street"
              type="text"
              value={formData.address.street}
              onChange={handleChange}
              required
            />
          </label>

          <label style={labelStyle}>
            City:
            <input
              style={inputStyle}
              name="address.city"
              type="text"
              value={formData.address.city}
              onChange={handleChange}
              required
            />
          </label>

          <label style={labelStyle}>
            Postal Code:
            <input
              style={inputStyle}
              name="address.postalCode"
              type="text"
              value={formData.address.postalCode}
              onChange={handleChange}
              required
            />
          </label>

          <label style={labelStyle}>
            Country:
            <input
              style={inputStyle}
              name="address.country"
              type="text"
              value={formData.address.country}
              onChange={handleChange}
              required
            />
          </label>
        </fieldset>

        <label style={labelStyle}>
          Description:
          <textarea
            style={textareaStyle}
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows={4}
          />
        </label>

        <button
          type="submit"
          style={{
            width: '100%',
            padding: '12px',
            backgroundColor: '#2563eb',
            color: 'white',
            fontWeight: 'bold',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Add Restaurant
        </button>
      </form>
    </div>
  );
};

export default AddRestaurant;
